add_filter('the_content','localportfoliocontent',9999);
function localportfoliocontent($content){
  global $post;
  global $wp_query;
  if(($post->ID == get_option('lsp-ptemplate-id')) && $wp_query->query_vars['city']=='' && (!(strstr($_SERVER['REQUEST_URI'],"feedback")))){
    $newContent = wp_kses(get_option('lsp_content'),array( 'a' => array( 'href'=>array(),'title'=>array())));
    $newContent = preg_replace('/style=(["\'])[^\1]*?\1/i','',$newContent, -1);
    $newContent = strip_tags($newContent,'<a><div><p>');

    return $newContent;    
  }
  else return $content;
}




add_filter('the_content','featuredpostscontent',9999);
function featuredpostscontent($content){
  global $post;
  global $wp_query;
  if(($post->ID == get_option('lsp-ptemplate-id2')) &&  $wp_query->query_vars['city']=='' && (!(strstr($_SERVER['REQUEST_URI'],"feedback")))){
    $newContent = "";

    $theseTypes = 'post';
    if(get_option('lsp-archive-types')) $theseTypes = explode(",","'post','".str_replace(",","','",get_option('lsp-archive-types'))."'");
    //print_r($theseTypes);
    //die("whatsup");
    $paged = (get_query_var('paged')) ? get_query_var('paged'):1;
    $my_query = new WP_Query(array('post_type' => $theseTypes, 'orderby' => 'meta_value_num', 'meta_key' => '_seo-visits-count', 'order' => 'DESC', 'paged'=> $paged));
    if($my_query->have_posts()){
      while($my_query->have_posts()){
        $my_query->the_post();
        $newContent .= "<a href='".get_the_permalink()."'>".get_the_title()."</a><br>".get_the_excerpt()."<br><br>";
      }
      if(get_previous_posts_link())$newContent .= get_previous_posts_link(__('<< Previous Page','lsp_text_domain'))." | ";
      $newContent .= get_next_posts_link( __('Show More Posts >>','lsp_text_domain') ,$my_query->max_num_pages) ;

    }
    wp_reset_postdata();
    return $content . $newContent;    
  }
  else return $content;
}



