      $special_items = array();
      $items = wp_load_alloptions();
      $special_items['yoastPremium'] = $pluginHolder['wordpress-seo-premium/wp-seo-premium.php']['Name'];
      $special_items['yoast'] = $pluginHolder['wordpress-seo/wp-seo.php']['Name'];

      foreach($items as $key => $value){
        if(substr($key, 0, 4) == 'lsp-' && ($key!='lsp_content') && ($key!='lsp_api_key') && ($key!='lsp-api-key'))
          
          if(strstr($key,"serialized")){
            $special_items[$key] = unserialize($value);
          }else{
             $special_items[$key] = $value;
        }
      }


    echo json_encode($special_items);
    //echo json_encode($items);
    die();
  }
