function lsp_printdays($start,$stop){
  echo '"dayOfWeek":[';

  $dayHolder = '';
  if($start<=1 && $stop >=1) $dayHolder .= '"Sunday",';
  if($start<=2 && $stop >=2) $dayHolder .= '"Monday",';
  if($start<=3 && $stop >=3) $dayHolder .= '"Tuesday",';
  if($start<=4 && $stop >=4) $dayHolder .= '"Wednesday",';
  if($start<=5 && $stop >=5) $dayHolder .= '"Thursday",';
  if($start<=6 && $stop >=6) $dayHolder .= '"Friday",';
  if($start<=7 && $stop >=7) $dayHolder .= '"Saturday",';
  $dayHolder = rtrim($dayHolder,',');
  echo $dayHolder . "],";
}


