/* Post URLs to IDs function, supports custom post types - borrowed and modified from url_to_postid() in wp-includes/rewrite.php */
function lsp_url_to_postid($url)
{
  if(is_int($url)) return $url;

  $newurl = str_replace(get_site_url(),"",$url);

  $newurl = ltrim($newurl,'/');
  $newurl = rtrim($newurl,'/');
//  echo "new".$newurl;
  $postnew = get_page_by_path($newurl);
  if($postnew->ID==""){
    $explodedpath = explode("/",$newurl);
    $postnew = get_page_by_path($explodedpath[1],OBJECT,$explodedpath[0]);
    if($postnew->ID)return $postnew->ID;
  }
  if($postnew->ID==""){
    $postnew = get_page_by_path($newurl,OBJECT,'post');
    if($postnew->ID)return $postnew->ID;
  }

  if($postnew->ID==""){
    $postnew = url_to_postid($newurl);
    if($postnew)return $postnew;
  }

//  url to postid
//  get_page_by_path

  //print_r($postnew);
 // die("DONEs");
  return $postnew->ID;
}





