function lspfp_widget_content(){
  global $post;
  global $wp_query;
    $newContent = "";

    $theseTypes = 'post';
    if(get_option('lsp-archive-types')) $theseTypes = explode(",","'post','".str_replace(",","','",get_option('lsp-archive-types'))."'");
    //print_r($theseTypes);
    //die("whatsup");
    $paged = (get_query_var('paged')) ? get_query_var('paged'):1;
    $my_query = new WP_Query(array('post_count'=>5, 'post_type' => $theseTypes, 'orderby' => 'meta_value_num', 'meta_key' => '_seo-visits-count', 'order' => 'DESC', 'paged'=> $paged));
    if($my_query->have_posts()){
      while($my_query->have_posts()){
        $my_query->the_post();
        $newContent .= "<a href='".get_the_permalink()."'>".get_the_title()."</a><br>";
      }
    if(get_option('lsp-widgetlink')!="no")$newContent .= "<a href='".get_the_permalink(get_option('lsp-ptemplate-id2'))."'>".__('More Featured Posts >>','lsp_text_domain')."</a><br>";
    }
    wp_reset_postdata();
    echo $newContent;    
}

